import { __decorate } from "tslib";
import { Component } from '@angular/core';
let ModalContentComponent = class ModalContentComponent {
    constructor(cartService, productsService, paymentModalService, userManagementService) {
        this.cartService = cartService;
        this.productsService = productsService;
        this.paymentModalService = paymentModalService;
        this.userManagementService = userManagementService;
        this.input = '';
        this.modalStage = 0;
        this.city = '';
        this.street = '';
        this.houseNumber = '';
        this.contactInfo = '';
        this.oldCartId = '';
        this.highlightSearch = () => {
            for (let index = 0; index < this.cartService.items.length; index++) {
                const item = document.getElementById(this.cartService.items[index]._id);
                (item.children[0].children[1].innerHTML.toLowerCase().includes(this.input.toLowerCase())) ?
                    item.classList.add("highlight") :
                    item.classList.remove("highlight");
                if (this.input == '') {
                    item.classList.remove("highlight");
                }
            }
        };
        this.autoFillData = (key) => {
            switch (key) {
                case 'city':
                    this.city = this.userManagementService.userData[key];
                    break;
                case 'street':
                    this.street = this.userManagementService.userData[key];
                    break;
                case 'houseNumber':
                    this.houseNumber = this.userManagementService.userData[key];
                    break;
            }
        };
        this.continuePayment = () => {
            // validations
            this.modalStage++;
        };
        this.finishPayment = async () => {
            const orderDetails = {
                city: this.city,
                street: this.street,
                houseNumber: this.houseNumber,
                shippingDate: this.shippingDate,
                contactInfo: this.contactInfo,
            };
            this.cartService.closeCart(orderDetails);
        };
        this.goBack = () => this.modalStage--;
        this.formatDate = () => {
            let today = new Date();
            let yyyy = today.getFullYear();
            let mm = today.getMonth() + 1; // Months start at 0!
            let dd = today.getDate();
            if (dd < 10)
                dd = '0' + dd;
            if (mm < 10)
                mm = '0' + mm;
            today = yyyy + '-' + mm + '-' + dd;
            return today;
        };
    }
    ngOnInit() {
        this.currentDate = this.formatDate();
        console.log(this.currentDate);
        this.oldCartId = this.cartService.cartData._id;
    }
};
ModalContentComponent = __decorate([
    Component({
        selector: 'app-modal-content',
        templateUrl: './modal-content.component.html',
        styleUrls: ['./modal-content.component.css']
    })
], ModalContentComponent);
export { ModalContentComponent };
