import * as i0 from "@angular/core";
import * as i1 from "src/app/services/user-management.service";
import * as i2 from "@angular/forms";
export class RegisterComponent {
    constructor(userManagementService) {
        this.userManagementService = userManagementService;
        this.confirmPassword = '';
        this.isUniqueId = null;
        this.registerPayload = {
            userId: ''
        };
        this.isNextAllowed = async () => {
            try {
                this.isUniqueId = await this.userManagementService.isIdUnique(this.registerPayload.userId);
                console.log(this.userManagementService.temp);
            }
            catch (error) {
                alert('please try again later');
            }
        };
        this.handleRegisterRequest = () => {
            this.userManagementService.initRegister(this.registerPayload);
            this.registerPayload = {};
        };
    }
    ngOnInit() {
        const prevBtns = document.querySelectorAll(".btn-prev");
        const nextBtns = document.querySelectorAll(".btn-next");
        const progress = document.getElementById("progress");
        const formSteps = document.querySelectorAll(".form-step");
        const progressSteps = document.querySelectorAll(".progress-step");
        let formStepsNum = 0;
        nextBtns.forEach((btn) => {
            btn.addEventListener("click", () => {
                if (this.isUniqueId) {
                    formStepsNum++;
                    updateFormSteps();
                    updateProgressbar();
                }
            });
        });
        prevBtns.forEach((btn) => {
            btn.addEventListener("click", () => {
                formStepsNum--;
                updateFormSteps();
                updateProgressbar();
            });
        });
        function updateFormSteps() {
            formSteps.forEach((formStep) => {
                formStep.classList.contains("form-step-active") &&
                    formStep.classList.remove("form-step-active");
            });
            formSteps[formStepsNum].classList.add("form-step-active");
        }
        function updateProgressbar() {
            progressSteps.forEach((progressStep, idx) => {
                if (idx < formStepsNum + 1) {
                    progressStep.classList.add("progress-step-active");
                }
                else {
                    progressStep.classList.remove("progress-step-active");
                }
            });
            const progressActive = document.querySelectorAll(".progress-step-active");
            progress.style.width =
                ((progressActive.length - 1) / (progressSteps.length - 1)) * 100 + "%";
        }
    }
}
