import { __decorate } from "tslib";
import { Component } from '@angular/core';
let RegisterComponent = class RegisterComponent {
    constructor(userManagementService) {
        this.userManagementService = userManagementService;
        this.confirmPassword = '';
        this.phase = false;
        this.registerPayload = {
            userId: '',
            password: '',
            confirmPassword: '',
            email: '',
            city: '',
            street: '',
            houseNumber: '',
        };
        this.handleBackClick = () => this.phase = true;
        this.handleRegisterRequest = () => {
            this.userManagementService.initRegister(this.registerPayload);
            this.registerPayload = {};
        };
        this.validateFirstPhaseInput = async () => {
            let errorMessage = "";
            this.registerPayload.userId.length != 9 ?
                errorMessage += "please enter a valid id number \n" : '';
            this.registerPayload.password.length < 6 ?
                errorMessage += 'password length must be at least 6 characters \n' : '';
            this.registerPayload.password != this.confirmPassword ?
                errorMessage += 'passwords must match \n' : '';
            if (errorMessage.length)
                return alert(errorMessage);
            this.phase = await this.userManagementService.isIdUnique(this.registerPayload.userId);
            console.log(this.userManagementService.isIdUnique(this.registerPayload.userId));
        };
    }
    ngOnInit() { }
};
RegisterComponent = __decorate([
    Component({
        selector: 'app-register',
        templateUrl: './register.component.html',
        styleUrls: ['./register.component.css']
    })
], RegisterComponent);
export { RegisterComponent };
